$(document).ready(function() {
	$("#drop-enquiry-form").submit(function(e) {
		e.preventDefault();
		e.stopPropagation();
		$.ajax({
			type : "POST",
			url : "/ajax/send/enquiry",
			data : $('#drop-enquiry-form').serialize(),
			error : function(xhr) {
				console.log(xhr.responseJSON);
			},
			complete : function(xhr, textStatus) {
				console.log(xhr.status);
			},
			success : function(data) {
				$("#drop-enquiry-form")[0].reset();
				$("#enqiry-form-success").text("Enquiry sent successfully")
			}
		})
		return false;

	})
})